import React from 'react';
import { Typography, Button } from '@material-ui/core';
import '../component_style/Organizer.css';

/**
 * Event View, unimplemented
 * TODO: read existing events from database and render
 */
export default class ViewEvent extends React.Component {

    constructor() {
        super();
        this.state = {
            entries: [
                "Entry 1",
                "Entry 2",
                "Entry 3"
            ]
        };
    }

    goBack = () => {
        this.props.handler(this.props.orgViews.MAIN);
    }

    render() {
        let entriesList = this.state.entries.map(entry => {
            return (<div>{entry}</div>)
        })
        return(
            <div>
                <Typography variant="h4" align='center' gutterBottom>Event XXX</Typography>
                <div className="eventDetails">
                    <Typography variant='h5' align='left'>Event Details</Typography>
                    <div><b>Event Name</b>: Event XXX</div>
                    <div> Date(s): _______</div>
                    <div className="viewEntries">
                        {entriesList}
                    </div>
                    <Button 
                        variant="contained"
                        className="buttons"
                        type="button"
                        onClick={this.goBack} >
                        Back
                    </Button>
                </div>
            </div>
        );
    }
}
