import React from 'react';
import { Typography, Button } from '@material-ui/core';
import '../component_style/Organizer.css';

/**
 * Event View, unimplemented
 * TODO: read existing events from database and render
 */
export default class ViewEvent extends React.Component {

    constructor() {
        super();
        this.state = {
            entries: [
                "Entry 1 (Dates attending) - Presenter 1, Presenter 2",
                "Entry 2 (Dates attending) - Presenter 1",
                "Entry 3 (Dates attending) - Presenter 1, Presenter 2"
            ]
        };
    }

    goBack = () => {
        this.props.handler(this.props.orgViews.MAIN);
    }

    handleClick = (e) => {
        console.log('entries clicked');
    }

    render() {
        let entriesList = this.state.entries.map(entry => {
            return (<div>{entry}</div>)
        })
        return(
            <div>
                <Typography variant="h4" align='center' gutterBottom>Sample Event</Typography>
                <div className="eventDetails">
                    <Typography variant='h5' align='left'> Details</Typography>
                    <div><b>Event Name</b>: Sample Event</div>
                    <div><b>Date(s)</b>: January 30 - Feb 2</div>
                    <div><b>Location</b>: Atlanta, GA</div>
                    <div className="viewEntries" onClick={this.handleClick}>
                        <div><b>Entries</b></div>
                        <div className="entriesList">
                            {entriesList}
                        </div>
                    </div>
                    <Button 
                        variant="contained"
                        className="buttons"
                        type="button"
                        onClick={this.goBack} >
                        Back
                    </Button>
                </div>
            </div>
        );
    }
}
